import React, { Component } from "react";
import { FormattedMessage } from "react-intl";
import { connect } from "react-redux";
import { LANGUAGES } from "../../../utils";
import * as actions from "../../../store/actions";
import './UserRedux.scss';
import { selectFilter } from "react-bootstrap-table2-filter";

class UserRedux extends Component {
  constructor(props) {
    super(props);
    this.state = {
      genderArr: [],
      positionArr: [],
      role: [],
      previewImgURL: '',
    };
  }

  async componentDidMount() {
    this.props.getGenderStart();
    this.props.getPositionStart();
    this.props.getRoleStart();
    // this.props.dispatch(action.getGenderStart())
    // try{
    //         let res = await getAllCodeServiece('gender');
    //         if(res && res.errCode === 0 ){
    //             this.setState({
    //                 genderArr: res.data
    //             })
    //         }
    //         console.log('thudb check res: ', res)
    // }catch(e){
    //     console.log(e)
    // }
  }
  // update gender trong compe lk voi redux
  componentDidUpdate(prevProps, prevState, snapshot) {
    //render => diupdate
    //hiện tại (this) và quá khứ (previous)
    //[] [3] phần tử
    // [3] [3]
    if (prevProps.genderRedux !== this.props.genderRedux) {
      this.setState({
        genderArr: this.props.genderRedux,
      });
    }

    if (prevProps.positionRedux !== this.props.positionRedux) {
      this.setState({
        positionArr: this.props.positionRedux,
      });
    }

    if (prevProps.roleRedux !== this.props.roleRedux) {
      this.setState({
        roleArr: this.props.roleRedux,
      });
    }
  }

  handleonChangeImage = ( event ) =>{
    let data = event.target.files;
    let file = data[0];
    if(file){
      
    }
    let objectUrl = URL.createObjectURL(file)
    console.log('check files 0:', objectUrl)
  }


  //router quản lý user redux
  render() {
    let language = this.props.language;
    let genders = this.state.genderArr;
    let roles = this.state.roleArr;
    let positions = this.state.positionArr;
    let isLoadingGender = this.props.isLoadingGender;
    console.log("check state compoment: ", this.state);
    return (
      <div className="user-redux-container">
        <div className="title">Minh đẹp trai có nhiều em ghệ đích bự</div>
        <div className="user-redux-body">
          <div className="container">
            <div className="row">
              <div className="col-12">
                <FormattedMessage id="manage-user.add" />{" "}
              </div>
              <div className="col-12">
                {isLoadingGender === true ? "Loading gender" : ""}{" "}
              </div>
              <form class="row g-3">
                <div class="col-3">
                  <label for="inputEmail4" class="form-label">
                    <FormattedMessage id="manage-user.email" />
                  </label>
                  <input type="email" class="form-control" id="inputEmail4" />
                </div>

                <div class="col-3">
                  <label for="inputPassword4" class="form-label">
                    <FormattedMessage id="manage-user.password" />
                  </label>
                  <input
                    type="password"
                    class="form-control"
                    id="inputPassword4"
                  />
                </div>

                <div class="col-3">
                  <label for="inputfirstName" class="form-label">
                    <FormattedMessage id="manage-user.first-name" />
                  </label>
                  <input type="text" class="form-control" id="inputfirstName" />
                </div>

                <div class="col-3">
                  <label for="inputlastName" class="form-label">
                    <FormattedMessage id="manage-user.last-name" />
                  </label>
                  <input type="text" class="form-control" id="inputlastName" />
                </div>

                <div class="col-3">
                  <label for="inputphoneNumber" class="form-label">
                    <FormattedMessage id="manage-user.phone-number" />
                  </label>
                  <input
                    type="text"
                    class="form-control"
                    id="inputphoneNumber"
                  />
                </div>

                <div class="col-9">
                  <label for="inputAddress" class="form-label">
                    <FormattedMessage id="manage-user.address" />
                  </label>
                  <input type="text" class="form-control" id="inputAddress" />
                </div>

                <div class="col-3">
                  <label for="inputGender" class="form-label">
                    <FormattedMessage id="manage-user.gender" />
                  </label>
                  <select id="inputGender" class="form-select">
                    {genders &&
                      genders.length > 0 &&
                      genders.map((item, index) => {
                        return (
                          <option key={index}>
                            {language === LANGUAGES.VI
                              ? item.valueVi
                              : item.valueEn}
                          </option>
                        );
                      })}
                  </select>
                </div>

                <div class="col-3">
                  <label for="inputPosition" class="form-label">
                    <FormattedMessage id="manage-user.position" />
                  </label>
                  <select id="inputPosition" class="form-select">
                  {positions && positions.length > 0 && positions.map((item, index) => {
                        return (
                          <option key={index}>
                            {language === LANGUAGES.VI ? item.valueVi : item.valueEn}
                          </option>
                        );
                      })}
                  </select>
                </div>

                <div class="col-3">
                  <label for="inputRoleID" class="form-label">
                    <FormattedMessage id="manage-user.role" />
                  </label>
                  <select id="inputRoleID" class="form-select">
                    {roles && roles.length > 0 && roles.map((item, index) => {
                        return (
                          <option key={index}>
                            {language === LANGUAGES.VI ? item.valueVi : item.valueEn}
                          </option>
                        );
                      })}
                  </select>
                </div>

                <div class="col-3">
                  <label for="inputImage" class="form-label">
                    <FormattedMessage id="manage-user.image" />
                    <div className="preview-img-container">
                    <input id="previewImg" type="file" hidden
                    onChange={(event) => this.handleonChangeImage(event)}
                    
                    
                    />           
                    <label className="label-upload" htmlFor="previewImg">Tải Ảnh <i class="fas fa-upload"></i></label>
                    <div className="preview-image"></div>
                    </div>
                  </label>
                </div>

                <div class="col-12">
                  <div class="form-check">
                    <input
                      class="form-check-input"
                      type="checkbox"
                      id="gridCheck"
                    />
                    <label class="form-check-label" for="gridCheck">
                      Check me out
                    </label>
                  </div>
                </div>
                <div class="col-12">
                  <button type="submit" class="btn btn-primary">
                    <FormattedMessage id="manage-user.save" />
                  </button>
                </div>
              </form>
            </div>
          </div>
        </div>
      </div>
    );
  }
}

const mapStateToProps = (state) => {
  return {
    language: state.app.language,
    genderRedux: state.admin.genders,
    roleRedux: state.admin.roles,
    positionRedux: state.admin.positions,
    isLoadingGender: state.admin.isLoadingGender,
  };
};

const mapDispatchToProps = (dispatch) => {
  return {
    getGenderStart: () => dispatch(actions.fetchGenderStart()),

    getPositionStart: () => dispatch(actions.fetchPositionStart()),

    getRoleStart: () => dispatch(actions.fetchRoleStart()),
    // processLogout: () => dispatch(actions.processLogout()),
    // changelanguageAppRedux: (language) => dispatch(actions.changelanguageApp(language))
  };
};

export default connect(mapStateToProps, mapDispatchToProps)(UserRedux);
